import React from 'react';

import MemberCard from './MemberCard';
import member1 from '../../images/member-1.jpg';
import member2 from '../../images/member-2.jpg';

function Member() {

    const members = [
        { 
            icon: member1, 
            memberName: '安部　晋三',
            description: 'ここに簡単な紹介文。お客様の心と髪に正直に向き合い、出来る限りのご要望にお応えします。"bj hair&dream"で髪を切ることが楽しいイベントになりますように。',
        },
        { 
            icon: member2, 
            memberName: 'Abe Shinzou',
            description: 'Use Replacer as a starting point in your next project or choose predesigned elements to customize',
        }
    ]

    return(
        <div className="member-area">
            <div className="member-box">
                <div className="title-box">
                    <h3 className="title">Members</h3>
                </div>
                <div className="flex-members-area">
                    {
                        members.map((member, index)=> {
                            return <MemberCard key={index} 
                                               icon={member.icon} 
                                               name={member.memberName} 
                                               description={member.description} 
                                    />
                        })
                    }
                </div> 
                <div className="contact-us">

                </div>
            </div>
        </div>
    );
}

export default Member;
